import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';

const Items = ({ itemsName, modalVisible, handleModalVisibility }) => {
    const [movies, setMovies] = useState([]);
    const [path, setPath] = useState('');

    useEffect(() => {
        setPath('/' + itemsName + '/123');
    }, [path]);

    const getMovies = async () => {
        let response = await fetch("https://api.themoviedb.org/3/movie/top_rated?api_key=041d9cd3501933c52b6696c13f0d60a7", {
            method: "GET",
        });
        response = await response.json();

        console.log(response.results.slice(0, 10));
        setMovies(response.results.slice(0, 10));
    }

    useEffect(() => {
        getMovies();
    }, []);

    return (
        <div>
            <p>{itemsName} COMPONENT!</p>
            <div>
            <ul>
                {
                    movies.map(movie => {
                        return <li key={movie.id}><NavLink onClick={() => handleModalVisibility(!modalVisible)} to={path}>{movie.title}</NavLink></li>
                    })
                }
                </ul>
            </div>
        </div>
    )
}

export default Items;
