import React, { useState, useEffect } from 'react';

const Movies = () => {
    const [movies, setMovies] = useState([]);

    const getMovies = () => {
        fetch("https://api.themoviedb.org/3/movie/top_rated?api_key=041d9cd3501933c52b6696c13f0d60a7&language=en-US&page=1", {
            method: "GET",
        }).then((res) => res.json()).then((res) => {
            //setMovies(res.results[0]);
            console.log(res.results[1]);
            return res.results[0];
        });
    }

    useEffect(() => {
        getMovies();
    });

    return (
        <p>Movies COMPONENT!</p>
    )
}

export default Movies;
