import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { MoviesContext } from '../contexts/MoviesContext';
import { SearchContext } from '../contexts/SearchContext';

const Movies = ({ modalVisible, handleModalVisibility }) => {
    const { topMovies, filteredMovies, showTopMovies, getFilteredMovies } = useContext(MoviesContext);
    const { term, setCurrentPath } = useContext(SearchContext);

    useEffect(() => {
        if(term != '') getFilteredMovies(term);
        setCurrentPath('/movies');

        let moviesTab = document.getElementById("movies-tab");
        moviesTab.classList.add("current-tab");
        
    }, [])

    return (
        <div>
            <p>MOVIES COMPONENT!</p>
            <div>
                <ul>
                    {
                        showTopMovies ? (
                            topMovies.map(movie => {
                                return (
                                    <li key={movie.id}>
                                        <NavLink onClick={() => handleModalVisibility(!modalVisible)} to={{ pathname: '/movies/details/' + movie.id, state: { movie: movie } }}>{movie.title}</NavLink>
                                    </li>
                                )
                            })
                        ) : (
                                filteredMovies.map(movie => {
                                    return (
                                        <li key={movie.id}>
                                            <NavLink onClick={() => handleModalVisibility(!modalVisible)} to={{ pathname: '/movies/details/' + movie.id, state: { movie: movie } }}>{movie.title}</NavLink>
                                        </li>
                                    )
                                })
                            )
                    }
                </ul>
            </div>
        </div>
    )
}

export default Movies;
