import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';

const Items = ({ itemsName, modalVisible, handleModalVisibility }) => {
    const [movies, setMovies] = useState([]);
    const [path, setPath] = useState('');

    useEffect(() => {
        setPath('/' + itemsName + '/123');
    });

    const getMovies = () => {
        fetch("https://api.themoviedb.org/3/movie/top_rated?api_key=041d9cd3501933c52b6696c13f0d60a7", {
            method: "GET",
        }).then((response) =>
            response.json()
        ).then((response) => {
            const topMovies = response.results.slice(0, 10);
            setMovies(topMovies);
            //console.log(typeof topMovies);
        });
    }

    useEffect(() => {
        getMovies();
    }, [movies]);

    return (
        <div>
            <p>{itemsName} COMPONENT!</p>
            <div>
                {
                    movies.map(movie => {
                        return <NavLink onClick={() => handleModalVisibility(!modalVisible)} to={path}>{movie.title}</NavLink>
                    })
                }
            </div>
        </div>
    )
}

export default Items;
