import React, { createContext, Component } from 'react';

export const TVShowsContext = createContext();

class TVShowsContextProvider extends Component {
    state = {
        shows: []
    }

    getShows = async () => {
        let response = await fetch("https://api.themoviedb.org/3/tv/popular?api_key=041d9cd3501933c52b6696c13f0d60a7", {
            method: "GET",
        });
        response = await response.json();

        const topShows = response.results.slice(0, 10);

        console.log(topShows);
        this.setState({
            shows: topShows
        });
    }

    componentDidMount() {
        this.getShows();
    }

    render() { 
        return (
            <TVShowsContext.Provider value={{...this.state}}>
                {this.props.children}
            </TVShowsContext.Provider>
        )
    }
}

export default TVShowsContextProvider;
