import userEvent from '@testing-library/user-event';
import React, { useContext, useRef } from 'react';
import { Link, NavLink, withRouter } from 'react-router-dom';
import '../css/Navbar.css';

const Navbar = () => {
    const moviesTab = useRef(null);
    const tvShowsTab = useRef(null);

    const setActiveTabStyle = (tab) => {
        if (tab == 'movies') {
            moviesTab.current.classList.add("current-tab");
            tvShowsTab.current.classList.remove("current-tab");
        }
        else {
            tvShowsTab.current.classList.add("current-tab");
            moviesTab.current.classList.remove("current-tab");
        }
    }

    return (
        <div id="navbar-container">
            <nav id="main-navigation">
                <ul id="navbar">
                    <li><NavLink ref={moviesTab} className="nav-link" to="/movies" onClick={setActiveTabStyle('movies')}>Movies</NavLink></li>
                    <li><NavLink ref={tvShowsTab} className="nav-link current-tab" to="/tvshows" onClick={setActiveTabStyle('tvshows')} > TV Shows</NavLink></li>
                </ul>
            </nav>
        </div>
    );
}

export default Navbar;
