import Navbar from './components/Navbar';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';
import Movies from './components/Movies';
import TVShows from './components/TVShows';
import DetailsModal from './components/DetailsModal';
import SearchBar from './components/SearchBar';
import MoviesContextProvider from './contexts/MoviesContext';
import TVShowsContextProvider from './contexts/TVShowsContext';
import SearchContextProvider from './contexts/SearchContext';

const App = () => {
  const [modalVisible, setModalVisible] = useState(false);

  const handleModalVisibility = (modalVisibility) => {
    setModalVisible(modalVisibility);
  }

  return (
    <div>
      <BrowserRouter>
        <SearchContextProvider>
          {
            modalVisible ? (
              <Switch>
                <Route exact path="/movies/details/:id" render={(props) => (<DetailsModal {...props} modalVisible={modalVisible} handleModalVisibility={handleModalVisibility} />)}></Route>
                <Route exact path="/tvshows/details/:id" render={(props) => (<DetailsModal {...props} modalVisible={modalVisible} handleModalVisibility={handleModalVisibility} />)}></Route>
              </Switch>
            )
              : (
                <MoviesContextProvider >
                  <TVShowsContextProvider>
                    <Navbar handleItemsName={handleItemsName} />
                    <SearchBar />

                    <Switch>
                      <Redirect exact from="/" to="/tvshows" />
                      <Route exact path="/movies" render={() => (<Movies modalVisible={modalVisible} handleModalVisibility={handleModalVisibility} />)} />
                      <Route exact path="/tvshows" render={() => (<TVShows modalVisible={modalVisible} handleModalVisibility={handleModalVisibility} />)} />
                    </Switch>
                  </TVShowsContextProvider>
                </MoviesContextProvider>
              )
          }
        </SearchContextProvider>
      </BrowserRouter>
    </div>
  );
}

export default App;
