import React, { useState, useEffect } from 'react';
import Modal from '../components/Modal';

const Movies = () => {
    const [movies, setMovies] = useState({});
    const [modalVisible, setModalVisible] = useState(true);

    const getMovies = () => {
        fetch("https://api.themoviedb.org/3/movie/top_rated?api_key=041d9cd3501933c52b6696c13f0d60a7&language=en-US&page=1", {
            method: "GET",
        }).then((response) => response.json()).then((response) => {
            const topMovies = response.results.slice(0,10);
            //setMovies(topMovies);
            console.log(topMovies);
            return topMovies;
        });
    }

    useEffect(() => {
        getMovies();
    }, [movies]);

    return (
        <div>
            {
                modalVisible ? <Modal visible={modalVisible}></Modal> : <p>Movies COMPONENT!</p> 
            }
            
        </div>
    )
}

export default Movies;
