import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { TVShowsContext } from '../contexts/TVShowsContext';
import { SearchContext } from '../contexts/SearchContext';

const TVShows = ({ modalVisible, handleModalVisibility }) => {
    const { topTVShows, filteredTVShows, showTopTVShows, getFilteredTVShows } = useContext(TVShowsContext);
    const { term } = useContext(SearchContext);

    useEffect(() => {
        if(term != '') getFilteredTVShows(term);
    }, [])

    return (
        <div>
            <p>TV SHOWS COMPONENT!</p>
            <div>
                <ul>
                    {
                        showTopTVShows ? (
                        topTVShows.map(tvShow => {
                            return (
                                <li key={tvShow.id}>
                                    <NavLink onClick={() => handleModalVisibility(!modalVisible)} to={{ pathname: '/tvshows/details/' + tvShow.id, state: { tvShow: tvShow } }}>{tvShow.name}</NavLink>
                                </li>
                            )
                        })
                        ) : (
                            filteredTVShows.map(tvShow => {
                            return (
                                <li key={tvShow.id}>
                                    <NavLink onClick={() => handleModalVisibility(!modalVisible)} to={{ pathname: '/tvshows/details/' + tvShow.id, state: { tvShow: tvShow } }}>{tvShow.name}</NavLink>
                                </li>
                            )
                        })
                        )
                    }
                </ul>
            </div>
        </div>
    )
}

export default TVShows;
