import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { MoviesContext } from '../contexts/MoviesContext';

const Items = ({ itemsName, modalVisible, handleModalVisibility }) => {
    const [path, setPath] = useState('');
    const { movies } = useContext(MoviesContext);

    useEffect(() => {
        setPath('/' + itemsName + '/123');
    }, [path]);

    return (
        <div>
            <p>{itemsName} COMPONENT!</p>
            <div>
            <ul>
                {
                    movies.map(movie => {
                        return <li key={movie.id}><NavLink onClick={() => handleModalVisibility(!modalVisible)} to={path}>{movie.title}</NavLink></li>
                    })
                }
                </ul>
            </div>
        </div>
    )
}

export default Items;
