import React, { useContext, useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import '../css/details.css';
import ReactPlayer from 'react-player';
import { MoviesContext } from '../contexts/MoviesContext';

const MovieDetails = (props) => {
   const [id, setId] = useState(null);
   const { movie } = props.location.state;
   const [currentPath, setCurrentPath] = useState(props.location.pathname);
   const [prevPath, setPrevPath] = useState('');
   const [videoUrl, setVideoUrl] = useState(null);
   const { getMovieTrailerUrl } = useContext(MoviesContext);

   useEffect(() => {
      let id = props.match.params.id;
      setId(id);
   }, [id]);

   useEffect(() => {
      setPrevPath('/' + currentPath.split('/')[1]);
   }, [currentPath]);

   useEffect(() => {
      var url = getMovieTrailerUrl(movie); //console.log(url);
      if (url) setVideoUrl(url);
   }, []);

   return (
      <div id='details-container'>
         <div id='image-wrapper'>
            <img
               src={'https://image.tmdb.org/t/p/w500' + movie.poster_path}
               alt='INot found...'
            ></img>
         </div>

         <div id='title-wrapper'>
            <h4>{movie.title}</h4>
         </div>

         <div id='goback-wrapper'>
            <div class='outer'>
               <div class='inner'>
                  <NavLink
                     id='goback-link'
                     onClick={() =>
                        props.handleModalVisibility(!props.modalVisible)
                     }
                     to={prevPath}
                  >
                     GO BACK
                  </NavLink>
               </div>
            </div>
         </div>

         <div id='overview-wrapper'>
            <p>
               <span class='fa fa-star star'></span>
               {' ' + movie.overview}
            </p>
         </div>

         {videoUrl ? (
            <div id='video-wrapper'>
               <ReactPlayer id='player' url={videoUrl}></ReactPlayer>
            </div>
         ) : (
            <div id='video-wrapper'>
               <img
                  id='alternative-image'
                  src={'https://image.tmdb.org/t/p/w500' + movie.backdrop_path}
                  alt='INot found...'
               ></img>
            </div>
         )}
      </div>
   );
};

export default MovieDetails;
