import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';

const DetailsModal = (props) => {
    const [id, setId] = useState(null);
    const { movie } = props.location.state;

    useEffect(() => { console.log(props.location);
        let id = props.match.params.id;
        setId(id);
    }, [id]);

    return (
        <div>
            {/* <NavLink onClick={() => props.handleModalVisibility(!props.modalVisible)} to={props.itemsPath}>GO BACK!</NavLink> */}
            <div>
                <img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt="Cover photo..."></img>
                <h4>{movie.title}</h4>
                <p>{movie.overview}</p>
            </div>
        </div>
    )
}

export default DetailsModal;
