const Image = ({ movie }) => {
    const [img, setImg] = useState(null);//"https://image.tmdb.org/t/p/w500" + movie.poster_path

    return (
        <div className="image-wrapper">
            <img src={setImg(this)} alt="Image not found..." ref={img2 => setImg(img2)} onError={()=>img.src = 'https://thumbs.dreamstime.com/b/hand-drawn-watercolor-question-mark-hand-drawn-watercolor-question-mark-isolated-white-background-161890817.jpg'} />
        </div>

    );
}

export default Image;
