import React, { useState, useEffect } from 'react';

const Items = ({itemsName, modalVisible, handleModalVisibility}) => {
    const [movies, setMovies] = useState({});

    const getMovies = () => {
        fetch("https://api.themoviedb.org/3/movie/top_rated?api_key=041d9cd3501933c52b6696c13f0d60a7&language=en-US&page=1", {
            method: "GET",
        }).then((response) => response.json()).then((response) => {
            const topMovies = response.results.slice(0,10);
            //setMovies(topMovies);
            console.log(topMovies);
            return topMovies;
        });
    }

    useEffect(() => {
        getMovies();
    }, [movies]);

    return (
        <div>
            <p>{itemsName[1]} COMPONENT!</p> 
            <button onClick={() => handleModalVisibility(!modalVisible)}>OPEN MODAL!</button>
        </div>
    )
}

export default Items;
