import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import '../css/details.css';

const TVShowDetails = (props) => {
    const [id, setId] = useState(null);
    const { tvShow } = props.location.state;
    const [currentPath, setCurrentPath] = useState(props.location.pathname);
    const [prevPath, setPrevPath] = useState('');

    useEffect(() => {
        let id = props.match.params.id;
        setId(id);
    }, [id]);

    useEffect(() => {
        setPrevPath('/' + currentPath.split('/')[1]);
    }, [currentPath]);

    return (
        <div id="details-container">
            <div id="image-wrapper">
                <img src={"https://image.tmdb.org/t/p/w500" + tvShow.poster_path} alt="Image not found..."></img>
            </div>

            <div id="title-wrapper">
                <h4>{tvShow.name}</h4>
            </div>

            <div id="goback-wrapper">
                <div class="outer">
                    <div class="inner">
                        <NavLink onClick={() => props.handleModalVisibility(!props.modalVisible)} to={prevPath}>GO BACK!</NavLink>
                    </div>
                </div>
            </div>

            <div id="overview-wrapper">
                <p>{tvShow.overview}</p>
            </div>
        </div>
    )
}

export default TVShowDetails;
