import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import '../css/details.css';

const MovieDetails = (props) => {
    const [id, setId] = useState(null);
    const { movie } = props.location.state;
    const [currentPath, setCurrentPath] = useState(props.location.pathname);
    const [prevPath, setPrevPath] = useState('');

    useEffect(() => {
        let id = props.match.params.id;
        setId(id);
    }, [id]);

    useEffect(() => {
        setPrevPath('/' + currentPath.split('/')[1]);
    }, [currentPath]);

    return (
        <div id="details-container">
            <div id="image-wrapper">
                <img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt="Image not found..."></img>
            </div>

            {/* <div id="title-wrapper">
                <h4>{movie.title}</h4>
            </div> */}

            <video src="https://www.youtube.com/watch?v=h6hZkvrFIj0"></video>

            <div id="goback-wrapper">
                <div class="outer">
                    <div class="inner">
                        <NavLink id="goback-link" onClick={() => props.handleModalVisibility(!props.modalVisible)} to={prevPath}>GO BACK</NavLink>
                    </div>
                </div>
            </div>

            <div id="overview-wrapper">
                <p>â¦¿{" " + movie.overview}</p>
            </div>
        </div>
    )
}

export default MovieDetails;
