import { useEffect, useState } from 'react';
import Navbar from './components/Navbar';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';
import Items from './components/Items';
import Details from './components/Details';
import SearchBar from './components/SearchBar';
import MoviesContextProvider from './contexts/MoviesContext';
import TVShowsContextProvider from './contexts/TVShowsContext';
import Modal from './components/Modal';

const App = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [itemsName, setItemsName] = useState('tvshows');
  const [itemsPath, setItemsPath] = useState('');
  const [itemDetailsPath, setItemDetailsPath] = useState('');

  useEffect(() => {
    setItemsPath('/' + itemsName);
    setItemDetailsPath('/' + itemsName + '/:id');
  });

  const handleItemsName = (name) => {
    setItemsName(name);
  }

  const handleModalVisibility = (modalVisibility) => {
    setModalVisible(modalVisibility);
  }

  return (
    <div>
      {
        modalVisible ? <Modal modalVisible={modalVisible} handleModalVisibility={handleModalVisibility}></Modal>
          : <BrowserRouter>
              <MoviesContextProvider >
                <TVShowsContextProvider>
                  <Navbar />
                  <SearchBar />

                  <Switch>
                    <Redirect exact from="/" to="/tvshows" />
                    <Route exact path={itemsPath} render={() => (<Items itemsName={itemsName} modalVisible={modalVisible} handleModalVisibility={handleModalVisibility}/>)} />                    
                    <Route exact path={itemDetailsPath} component={Details}></Route>
                  </Switch>
                </TVShowsContextProvider>
              </MoviesContextProvider>
            </BrowserRouter>
      }
    </div>
  );
}

export default App;
