import React, { useContext } from 'react';
import { Link, NavLink, withRouter } from 'react-router-dom';
import '../css/Navbar.css';

const Navbar = () => {
    const setActiveTabStyle = (tab) => {
        let moviesTab = document.getElementById("movies-tab");
        let tvShowsTab = document.getElementById("tvshows-tab");

        if (tab == 'movies') {
            moviesTab.classList.add("current-tab");
            tvShowsTab.classList.remove("current-tab");
        }
        else {
            tvShowsTab.classList.add("current-tab");
            moviesTab.classList.remove("current-tab");
        }
    }

    return (
        <div id="navbar-container">
            <nav id="main-navigation">
                <ul id="navbar">
                    <li><NavLink id="movies-tab" className="nav-link" to="/movies" onClick={setActiveTabStyle('movies')}>Movies</NavLink></li>
                    <li><NavLink id="tvshows-tab" className="nav-link current-tab" to="/tvshows" onClick={setActiveTabStyle('tvshows')} > TV Shows</NavLink></li>
                </ul>
            </nav>
        </div>
    );
}

export default Navbar;
