import userEvent from '@testing-library/user-event';
import React, { useContext, useEffect, useRef, useState } from 'react';
import { Link, NavLink, withRouter } from 'react-router-dom';
import '../css/navbar.css';

const Navbar = (props) => {
    const [moviesTabClass, setMoviesTabClass] = useState("nav-link");
    const [tvShowsTabClass, setTVShowsTabClass] = useState("nav-link current-tab");

    const setActiveTabStyle = (tab) => {
        if (tab == 'movies') {
            setMoviesTabClass('nav-link current-tab');
            setTVShowsTabClass('nav-link');
        }
        else {
            setTVShowsTabClass('nav-link current-tab');
            setMoviesTabClass('nav-link');
        }
    }
    
    useEffect(() => {
        console.log(props);
    }, []);

    return (
        <div id="navbar-container">
            <nav id="main-navigation">
                <ul id="navbar">
                    <li><NavLink className={moviesTabClass} to="/movies" onClick={() => setActiveTabStyle('movies')}>Movies</NavLink></li>
                    <li><NavLink className={tvShowsTabClass} to="/tvshows" onClick={() => setActiveTabStyle('tvshows')} > TV Shows</NavLink></li>
                </ul>
            </nav>
        </div>
    );
}

export default withRouter(Navbar);
