import React, { useEffect, useState, useContext } from 'react';
import { SearchContext } from '../contexts/SearchContext';
import { MoviesContext } from '../contexts/MoviesContext';
import { TVShowsContext } from '../contexts/TVShowsContext';
import { withRouter, useHistory } from 'react-router-dom';

const SearchBar = (props) => {
    const { term, setTerm } = useContext(SearchContext);
    const { filteredMovies, getFilteredMovies, showTopMovies, changeMoviesAppereance, setFilteredMovies } = useContext(MoviesContext);
    const { getFilteredTVShows, changeTVShowsAppereance } = useContext(TVShowsContext);
    const [path, setPath] = useState(props.location.pathname);
    const [history, setHistory] = useState(useHistory());

    // history.listen(()=>{
    //     props.location;
    // })

    const handleInputChange = (e) => {
        setTerm(e.target.value);
    }

    useEffect(() => {
        setPath(props.location.pathname);
    });

    useEffect(() => { console.log(props);
        if (term.length >= 3) {
            const timeout = setTimeout(() => {
                let query = term.split(" ");
                query = query.filter(q => q !== "");

                if (query.length > 1) {
                    query = query.toString();
                    query = query.replace(new RegExp("\\,", "g"), '+');
                }
                
                if(path === '/movies') getFilteredMovies(query);
                else getFilteredTVShows(query);
            }, 1000);

            return () => clearTimeout(timeout);
        }
        else {
            if(path === '/movies') changeMoviesAppereance(true);
            else changeTVShowsAppereance(true);
        }
    }, [term]);

    return (
        <div>
            Search bar!
            <input type="text" value={term} onChange={handleInputChange} placeholder="Search by name..."></input>
        </div>
    );
}

export default withRouter(SearchBar);
