import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { MoviesContext } from '../contexts/MoviesContext';
import { SearchContext } from '../contexts/SearchContext';
import '../css/items.css'

const Movies = ({ modalVisible, handleModalVisibility }) => {
    const { topMovies, filteredMovies, showTopMovies, getFilteredMovies } = useContext(MoviesContext);
    const { term, setCurrentPath } = useContext(SearchContext);

    useEffect(() => {
        if (term != '') getFilteredMovies(term);
        setCurrentPath('/movies');
    }, []);

    return (
        <div className="items-container">
            {
                showTopMovies ? (
                    topMovies.map(movie => {
                        return (
                            <NavLink className="item" onClick={() => handleModalVisibility(!modalVisible)} to={{ pathname: '/movies/details/' + movie.id, state: { movie: movie } }}>
                            <div className="item-link">
                                <div className="image-wrapper"><img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt="Cover photo..."></img></div>
                                <div className="item-link-wrapper">{movie.title}</div>
                            </div>
                            </NavLink>
                        )
                    })
                ) : (
                        filteredMovies.map(movie => {
                            return (
                                <div>
                                    <img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt="Cover photo..."></img>
                                    <NavLink onClick={() => handleModalVisibility(!modalVisible)} to={{ pathname: '/movies/details/' + movie.id, state: { movie: movie } }}>{movie.title}</NavLink>

                                </div>
                            )
                        })
                    )
            }
        </div>
    )
}

export default Movies;
