import React from 'react';

const Movies = () => {
    const [movies, setMovies] = useState([]);

    const getMovies = () => {
        fetch("https://api.themoviedb.org/3/movie/top_rated?api_key=041d9cd3501933c52b6696c13f0d60a7", {
            method: "GET",
        }).then((res) => res.json()).then((movies) => {
            setMovies(movies);
            console.log(movies);
            return movies;
        }).done();
    }

    useEffect(() => {
        getMovies();
    }, [movies]);

    return (
        <p>Movies COMPONENT!</p>
    )
}

export default Movies;
