import React, { useEffect, useState, useContext } from 'react';
import { SearchContext } from '../contexts/SearchContext';
import { MoviesContext } from '../contexts/MoviesContext';
import { TVShowsContext } from '../contexts/TVShowsContext';
import { withRouter } from 'react-router-dom';
import '../css/searchbar.css';

const SearchBar = (props) => {
    const { term, currentPath, setTerm } = useContext(SearchContext);
    const { filteredMovies, getFilteredMovies, showTopMovies, changeMoviesAppereance, setFilteredMovies } = useContext(MoviesContext);
    const { getFilteredTVShows, changeTVShowsAppereance } = useContext(TVShowsContext);

    const handleInputChange = (e) => {
        setTerm(e.target.value);
    }

    useEffect(() => {
        if (term.length >= 3) {
            const timeout = setTimeout(() => {
                /* let query = term.split(" ");
                query = query.filter(q => q !== "");

                if (query.length > 1) {
                    query = query.toString();
                    query = query.replace(new RegExp("\\,", "g"), '+');
                } */

                if (currentPath === '/movies') getFilteredMovies(term);
                else getFilteredTVShows(term);
            }, 1000);

            return () => clearTimeout(timeout);
        }
        else {
            if (currentPath === '/movies') changeMoviesAppereance(true);
            else changeTVShowsAppereance(true);
        }
    }, [term]);

    return (
         <div>

            <input type="text" value={term} onChange={handleInputChange} placeholder="Search by name..."><img src="https://cdn2.iconfinder.com/data/icons/font-awesome/1792/search-512.png"></img></input>
        </div>

        

    );
}

export default withRouter(SearchBar);
