import {useState} from 'react';

const Image = ({movie}) => {
    const { imageLoaded, setImageLoaded } = useState(true); 

    return (
        <div className="image-wrapper">
            {
                imageLoaded ? <img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt="Cover photo..." onLoad={(e) => console.log(e)}></img>  
                : null
                 
            }
            
        </div>
        
    );
}
 
export default Image;
