import userEvent from '@testing-library/user-event';
import React, { useContext, useRef, useState } from 'react';
import { Link, NavLink, withRouter } from 'react-router-dom';
import '../css/Navbar.css';

const Navbar = () => {
    const [moviesTabClass, setMoviesTabClass] = useState("nav-link");
    const [tvShowsTabClass, setTVShowsTabClass] = useState("nav-link current-tab");

    const setActiveTabStyle = (tab) => {
        if (tab == 'movies') {
            setMoviesTabClass(moviesTabClass += ' current tab');
            setTVShowsTabClass(tvShowsTabClass.split('')[0]);
        }
        else {
            setMoviesTabClass(moviesTabClass.split('')[0]);
            setTVShowsTabClass(tvShowsTabClass += 'current-tab');
        }
    }

    return (
        <div id="navbar-container">
            <nav id="main-navigation">
                <ul id="navbar">
                    <li><NavLink ref={moviesTab} className={moviesTabClass} to="/movies" onClick={() => setMoviesTabClass('movies')}>Movies</NavLink></li>
                    <li><NavLink ref={tvShowsTab} className={tvShowsTabClass} to="/tvshows" onClick={setActiveTabStyle('tvshows')} > TV Shows</NavLink></li>
                </ul>
            </nav>
        </div>
    );
}

export default Navbar;
